import React, { useState, useEffect } from "react";
import './App.css';
import hash from "./hash";
import Stats from "./Stats.js";
import { authEndpoint, clientId, redirectUri, scopes } from "./config_example";


function App() {

  const [token, setToken] = useState(null);

  const [topTracks, setTopTracks] = useState([]);
    const [topTracksActivated, setTopTracksActivated] = useState(false);
    const [topArtists, setTopArtists] = useState([]);
    const [topArtistsActivated, setTopArtistsActivated] = useState(false);
    const [data, setData] = useState({});
    const ENDPOINT = "https://api.spotify.com/v1/me/top/artists";
    const headers = {
        Accept: 'application/json',
            Authorization: 'Bearer ' + token,
            'Content-Type': 'application/json'
    };
    const artists = [];


    const getTopArtists = async (token) => {


        await  axios
        .get(ENDPOINT, {
          params: { limit: 500, offset: 0 },
          headers: headers,
        })
        .then((response) => {
            // const topArtists = response.data;
            // console.log(response.data);
            // this.setState({ topArtists });
            setData(response.data);

        })

    }



  useEffect(() => {
    var mToken = hash.access_token;
    if (mToken) {
      setToken(mToken);
    }

  })
  return (
    <div className="App">
      <body className="App-body">
        {!token && (
          <button onClick={getTopArtists}>
            <a
              className="btn btn--loginApp-link"
              href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join(
                "%20"
              )}&response_type=token&show_dialog=true`}
            >
              Login to Spotify
            </a>
          </button>
        )}
        {token && (
          <Stats topArtists={topArtists} />
        )}
      </body>
    </div>
  );
}

export default App;
